import React from 'react'
import styled from 'styled-components'

export const ChampionProfile = (props) => {
    const {year, tournament, isHeld, name, country, highlight, image} = props
  return (
    <ProfileWrapper>
    <ProfileTextarea>
        <p className="openName">{`${year}年　${tournament}`}</p>
        <p className="playerName">
          {isHeld ? name : "まだ開催されていません。"}
          {country && <span className="country">{`（${country}）`}</span>}
        </p>
        {isHeld ? highlight && <a href={highlight} className="highlight" target='blank'>決勝戦ハイライト</a> : <p>誰が優勝すると思いますか？</p>}
      </ProfileTextarea>
      {isHeld ? <img src={image} alt={name} /> : <img src="images/dummyjpg" />}
  </ProfileWrapper>
  )
}

const ProfileWrapper = styled.div`
    padding-top: 50px;
    display: flex;
    flex-direction: row-reverse;
    justify-content: center;
    max-width: 840px;
    margin-left: auto;
    margin-right: auto;
    img{
        aspect-ratio: 1;
        object-fit: cover;
        width: calc(45% - 50px);
        padding-right: 50px;
    }
`

const ProfileTextarea = styled.div`
    width: 55%;
    padding-top: 70px;
`
