import { SelectOption } from '../molecules/SelectOption'
import { SearchButton } from '../atoms/SearchButton'
import styled from 'styled-components'

export const SelectArea = (props) => {
    // const {options, selected, onChange, children} = props
    const {yearOptions, tournamentOptions, selectedYear, selectedTournament, onChangeYear, onChangeTournament, onClick} = props
  return (
    <SSelectArea>
        <SelectOption
          options={yearOptions}
          defaultValue={selectedYear}
          onChange={onChangeYear}
        >
          開催年
        </SelectOption>
        <SelectOption
          options={tournamentOptions}
          defaultValue={selectedTournament}
          onChange={onChangeTournament}
        >
          大会
        </SelectOption>
        <SearchButton onClick={onClick}>検索する</SearchButton>
    </SSelectArea>
  )
}

const SSelectArea = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  padding-top: 30px;
`
