import React, { useState } from 'react';
import { GameChampionData, gameChampionDataType } from './types/gameChampionData';
import { SearchButton } from './ components/atoms/SearchButton';
import { HeaderText } from './ components/atoms/HeaderText';
import { SelectOption } from './ components/molecules/SelectOption';
import { Year, yearType } from './types/year';
import { Tournament, tournamentType } from './types/tournament';
import { ChampionProfile } from './ components/organisms/ChampionProfile';

const App = () => {

  const [champion, setChampion] = useState<gameChampionDataType>(GameChampionData[9]);
  const [selectedYear, setSelectedYear] = useState<yearType>(Year[9]);
  const [selectedTournament, setSelectedTournament] = useState<tournamentType>(Tournament[0]);

  const onChangeYear = (value: yearType) => setSelectedYear(value);  

  const onChangeTournament = (value: tournamentType) =>  setSelectedTournament(value);

  const onClickSearch = () => {
    const newPlayer = GameChampionData.filter(({year}) => year === selectedYear.value).find(({tournament}) => tournament === selectedTournament.value) || champion
    setChampion(newPlayer)
  };

  return (
    <div className="container">
      <HeaderText>
          グランドスラムの優勝者を検索するアプリです。
          <br />開催年と大会を選択してください。
      </HeaderText>
      <div className="selectArea">
        <SelectOption
          options={Year}
          defaultValue={selectedYear}
          onChange={onChangeYear}
        >
          開催年
        </SelectOption>
        <SelectOption
          options={Tournament}
          defaultValue={selectedTournament}
          onChange={onChangeTournament}
        >
          大会
        </SelectOption>
        <SearchButton onClick={() => onClickSearch()}>検索する</SearchButton>
      </div>
      <ChampionProfile
        year={champion.year}
        tournament={champion.tournament}
        isHeld={champion.isHeld}
        name={champion.name}
        country={champion.country}
        highlight={champion.highlight}
        image={champion.imageSrc}
      />
      {/* <div className="profile">
        <div className="textarea">
            <p className="openName">{`${champion.year}年　${champion.tournament}`}</p>
            <p className="playerName">
              {champion.isHeld ? champion.name : "まだ開催されていません。"}
              {champion.country && <span className="country">{`（${champion.country}）`}</span>}
            </p>
            {champion.isHeld ? champion.highlight && <a href={champion.highlight} className="highlight" target='blank'>決勝戦ハイライト</a> : <p>誰が優勝すると思いますか？</p>}
          </div>
          {champion.isHeld ? <img src={champion.imageSrc} alt={champion.name} /> : <img src="images/dummy.jpg" />}
      </div> */}
    </div>
  );
}

export default App;
