import React, { useState } from 'react';
import Select from 'react-select'
import { championData, championDataType } from './types/championData';

const yearOptions = [
  {value: 2015, label: 2015},
  {value: 2016, label: 2016},
  {value: 2017, label: 2017},
  {value: 2018, label: 2018},
  {value: 2019, label: 2019},
  {value: 2020, label: 2020},
  {value: 2021, label: 2021},
  {value: 2022, label: 2022},
  {value: 2023, label: 2023},
  {value: 2024, label: 2024}
]

const tournamentOptions = [
  {value: 'australianOpen', label: '全豪オープン'},
  {value: 'frenchOpen', label: '全仏オープン'},
  {value: 'wimbledon', label: 'ウィンブルドン'},
  {value: 'usOpen', label: '全米オープン'}
]
const App = () => {

  const [ player, setPlayer ] = useState<championDataType>(championData[28])
  const [selectedValue, setSelectedValue] = useState(yearOptions[0]);
  const [selectedValue2, setSelectedValue2] = useState(tournamentOptions[0]);

  const onChangeYear = (value) => {
    return setSelectedValue(value);
  }

  const onChangeTournament = (value) => {
    return setSelectedValue2(value);

  }
  const onClick = () => {
    const newPlayer = championData.filter(({year}) => year === selectedValue.value ).find(({tournament}) => tournament === selectedValue2.value ) || player
    setPlayer(newPlayer)
    console.log(player)
    console.log(selectedValue)
    console.log(selectedValue2)
  }
  return (
    <div className="App">
      <div className="container">
        <p className="headText">
            グランドスラムの優勝者を検索するアプリです。
            <br />開催年と大会を選択してください。
        </p>
        <div className="selectArea">
            <div className="yearWrapper">
                <p className="year">開催年</p>
                <Select options={yearOptions} defaultValue={selectedValue} onChange={(value) => onChangeYear(value)} />
            </div>
            <div className="openWrapper">
                <p className="year">大会</p>
                <Select options={tournamentOptions} defaultValue={tournamentOptions[0]} onChange={() => onChangeTournament} />
            </div>
            <button className="button" onClick={() => onClick()}>検索する</button>
        </div>
        <div className="profile">
            <div className="textarea">
                <p className="openName">{`${player.year}年　${player.tournament}`}</p>
                <p className="playerName">{player.name}<span className="country">{`（${player.country}）`}</span></p>
                <a href={player.highlight} className="highlight">決勝戦ハイライト</a>
            </div>
            <img src={player.imageSrc} alt={player.name} />
        </div>
      </div>
    </div>
  );
}

export default App;
