import React, { useState } from 'react';
import Select from 'react-select'
import { championData, championDataType } from './types/championData';

const yearOptions = [
  {value: 2015, label: 2015},
  {value: 2016, label: 2016},
  {value: 2017, label: 2017},
  {value: 2018, label: 2018},
  {value: 2019, label: 2019},
  {value: 2020, label: 2020},
  {value: 2021, label: 2021},
  {value: 2022, label: 2022},
  {value: 2023, label: 2023},
  {value: 2024, label: 2024}
]

const tournamentOptions = [
  {value: 'australianOpen', label: '全豪オープン'},
  {value: 'frenchOpen', label: '全仏オープン'},
  {value: 'wimbledon', label: 'ウィンブルドン'},
  {value: 'usOpen', label: '全米オープン'}
]

const App = () => {

  const [player, setPlayer] = useState<championDataType>(championData[2])
  const [selectedYear, setSelectedYear] = useState(yearOptions[2]);
  const [selectedTournament, setSelectedTournament] = useState(tournamentOptions[0]);

  const onChangeYear = (value) => setSelectedYear(value);  

  const onChangeTournament = (value) =>  setSelectedTournament(value);

  const onClick = () => {
    const newPlayer = championData.filter(({year}) => year === selectedYear.value ).find(({tournament}) => tournament === selectedTournament.label ) || player
    setPlayer(newPlayer)
  }

  return (
    <>
      <div className="container">
        <p className="headText">
            グランドスラムの優勝者を検索するアプリです。
            <br />開催年と大会を選択してください。
        </p>
        <div className="selectArea">
            <div className="yearWrapper">
                <p className="year">開催年</p>
                <div className="selectWrapper">
                  <Select options={yearOptions} defaultValue={selectedYear} onChange={onChangeYear} />
                </div>
            </div>
            <div className="openWrapper">
                <p className="year">大会</p>
                <div className="selectWrapper">
                  <Select options={tournamentOptions} defaultValue={selectedTournament} onChange={onChangeTournament} />
                </div>
            </div>
            <button className="button" onClick={() => onClick()}>検索する</button>
        </div>
        <div className="profile">
          <div className="textarea">
              <p className="openName">{`${player.year}年　${player.tournament}`}</p>
              <p className="playerName">
                {player.isHeld ? player.name : "まだ開催されていません。"}
                {player.country && <span className="country">{`（${player.country}）`}</span>}
              </p>
              {player.isHeld ? player.highlight && <a href={player.highlight} className="highlight">決勝戦ハイライト</a> : <p>誰が優勝すると思いますか？</p>}
            </div>
            {player.isHeld ? <img src={player.imageSrc} alt={player.name} /> : <img src="images/dummy.jpg" />}
        </div>
      </div>
    </>
  );
}

export default App;
