import React, { useState } from 'react';
import { championData, championDataType } from './types/championData';
import { SearchButton } from './ components/atoms/SearchButton';
import { HeaderText } from './ components/atoms/HeaderText';
import { SelectOption } from './ components/molecules/SelectOption';
import { yearOptions, yearType } from './types/selectYear';
import { tournamentOptions, tournamentType } from './types/selectTournament';


const App = () => {

  const [player, setPlayer] = useState<championDataType>(championData[2]);
  const [selectedYear, setSelectedYear] = useState<yearType>(yearOptions[2]);
  const [selectedTournament, setSelectedTournament] = useState<tournamentType>(tournamentOptions[0]);

  const onChangeYear = (value) => setSelectedYear(value);  

  const onChangeTournament = (value) =>  setSelectedTournament(value);

  const onClick = () => {
    const newPlayer = championData.filter(({year}) => year === selectedYear.value ).find(({tournament}) => tournament === selectedTournament.label ) || player
    setPlayer(newPlayer)
  }

  return (
    <div className="container">
      <HeaderText>
          グランドスラムの優勝者を検索するアプリです。
          <br />開催年と大会を選択してください。
      </HeaderText>
      <div className="selectArea">
        <SelectOption
          options={yearOptions}
          defaultValue={selectedYear}
          onChange={onChangeYear}
        >
          開催年
        </SelectOption>
                  <SelectOption
          options={tournamentOptions}
          defaultValue={selectedTournament}
          onChange={onChangeTournament}
        >
          大会
        </SelectOption>
          <SearchButton onClick={() => onClick()}>検索する</SearchButton>
      </div>
      <div className="profile">
        <div className="textarea">
            <p className="openName">{`${player.year}年　${player.tournament}`}</p>
            <p className="playerName">
              {player.isHeld ? player.name : "まだ開催されていません。"}
              {player.country && <span className="country">{`（${player.country}）`}</span>}
            </p>
            {player.isHeld ? player.highlight && <a href={player.highlight} className="highlight" target='blank'>決勝戦ハイライト</a> : <p>誰が優勝すると思いますか？</p>}
          </div>
          {player.isHeld ? <img src={player.imageSrc} alt={player.name} /> : <img src="images/dummy.jpg" />}
      </div>
    </div>
  );
}

export default App;
